def caesar(direction1,plain_text,shift_amount):
    message= ""   
    #checking if theres something else than a letter  
    for char in plain_text: 
        if char in alphabet:
            position = alphabet.index(char)
            if direction1 == 'decode':
                #next funtion is created since the alpabeth has only 27 letters, so if client goes beyond that it will start over, without worrying about shift amount
                new_position = (position - shift_amount) % len(alphabet)
            else:
                new_position = (position + shift_amount) % len(alphabet)
            new_letter = alphabet[new_position]
            message += new_letter  
        else:
            message += char   
    print(f"The {direction}d messega is: {message}")     
